head.script:
scwin.onpageload = function() {
    // 알럿창 클래스 부여
    var alertElements = document.querySelectorAll('.alert');

    alertElements.forEach(function(alertElement) {
        var popupWindow = alertElement.closest('.w2popup_window');

        if (popupWindow) {
            popupWindow.classList.add('alert-pop');

            var modalSibling = popupWindow.previousElementSibling;
            if (modalSibling && modalSibling.classList.contains('w2modal')) {
                modalSibling.classList.add('opt');
            }
        }
    });
};
//팝업 닫기
scwin.closePopup_onclick = function(e) {
	$p.closePopup();
};
