head.script:
scwin.onpageload = function() {
	 // grp-add-box
            function addBoxVisibility(){
                $(".grp-add-box").each(function() {
                    const wrapAddBox = $(this);
                    const addBox = wrapAddBox.find(".add-box");

                    if (addBox.length === 0) {
                        wrapAddBox.remove();
                    }
                });
            };
    //round-table-box 안에 data가 없으면
            function tableVisibility(){
                 $(".round-table-box").each(function() {
                const wrapTableBox = $(this);
                const dataRow = wrapTableBox.find(".w2tb_td");

                if(dataRow.length === 0){
                    wrapTableBox.find('.tb').remove();
                    wrapTableBox.find('.nodata').show();
                }
                });
            };
    //upload-form-box 안에 data가 없으면
            function uploadVisibility(){
                 $(".upload-form-box").each(function() {
                const uploadBox = $(this);
                const dataRow = uploadBox.find(".w2tb_td");

                if(dataRow.length === 0){
                    uploadBox.find('.table-box').remove();
                    uploadBox.find('.notice').show();
                }
                });
            };
     // add-box 삭제       
			function deleteBtn() {
                    const $btn = $('.btn-delete');

                    $btn.on('click', function(){
                        if($(this).closest('.add-box')){
                            $(this).closest('.add-box').remove();
                        } 
                        if($(this).closest('table')){
                            $(this).closest('tr').remove();
                        }
                        addBoxVisibility();
                        tableVisibility();
                        uploadVisibility();
                    });
			};
    //버튼 있는 tab
            function tabBtn(){
                const buttonBox = $('<li class="tab-control-box w2tabcontrol_li"></li>');

                const tabAddBtn = $('<a href role="button" id="addTabLi" class="btn-dotline">추가하기</a>');
                const tabCopyPaste = $('<a href role="button" class="btn-dotline">복사 추가하기</a>');

                buttonBox.append(tabAddBtn);
                buttonBox.append(tabCopyPaste);

                $('.w2tabcontrol_tabhost').append(buttonBox);
            }
    //툴팁
            function tooltipOn(){
                const btnTooltipOn = $('.btn-tooltip');
                const btnToolTipOff = btnTooltipOn.next().find('.btn-closed');

                btnTooltipOn.on('click',function(){
                    $(this).next().fadeIn();
                });
                btnToolTipOff.on('click', function(){
                    $(this).closest('.balloon').fadeOut();
                });

            }

            addBoxVisibility();
            tableVisibility();
            uploadVisibility();
            deleteBtn();
            tabBtn();
            tooltipOn();
};
