		},{
			"seriesname":"SH",
			"color":"008800"
		}];


		var opt = chart32.getJSONData();//chart에 매핑된 data 값 
		var len = opt.dataset.length; 

		for( var i = 0 ; i< len ; i ++){
			var colorData = colorMatch.filter(function(item) { return item.seriesname === opt.dataset[i].seriesname ; }); 
			opt.dataset[i].color = colorData[0].color ;
		}

		chart32.fc().setJSONData(opt);

	};

